/**
 * Tags a function as a validator.
 */
export declare const Validator: symbol;
/**
 * Tags additional metadata to a function validator
 */
export declare const Metadata: symbol;
/**
 * Tags a validator function as a Type validator.
 * @example Type(String)
 */
export declare const TypeValidator: symbol;
/**
 * Tags a validator function as a Options validator.
 * @example Any, All
 */
export declare const OptionsValidator: symbol;
/**
 * Tags a validator function as a Custom validator.
 */
export declare const CustomValidator: symbol;
